import React, { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCopy } from '@fortawesome/free-solid-svg-icons'

function Display(props) {

  const handleCopy = ()=>{
    var copyText = props.password;
    copyText.select();
    props.password.setSelectionRange(0,99999) // For mobile devices
    navigator.clipboard.writeText(props.password.value);
  }

  return (
    <div className='w-full'>
        <div className='custom-shadow flex justify-between items-center custom-bg p-5 rounded-2xl text-yellow-400'>
          <h1 id="display-content" className='text-2xl font-semibold'>{props.password}</h1>
          <FontAwesomeIcon className="h-6" icon={faCopy} style={{color: "#23b2e1",}} onClick={handleCopy} />
        </div>
    </div>
  )
}

export default Display
