import React, { useEffect, useState } from 'react'

function Slider(props) {
    const [length, setLength] = useState(8);

    const handleChange = (e)=>{
        const value = parseInt(e.target.value, 10)
        setLength(value)
        props.setLength(e.target.value)
    }
    const handleIndicator = (len)=>{
        if(len <= 4){
            props.setIndicator("red")
          } else if(len <= 8){
            props.setIndicator("blue")
          } else {
            props.setIndicator("green")
          }
    }
    useEffect(()=>{
        handleIndicator(length)
    },[length])

  return (
    <div className="slidecontainer w-full">
        <input type="range" min="1" max="20" value={length} className="slider w-full " id="myRange" onChange={handleChange}/>
    </div>
  )
}

export default Slider
