import React, { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCopy } from '@fortawesome/free-solid-svg-icons'

function Display(props) {

  const [isCopied, setCopied] = useState(false)

  const show = ()=> {
    return <div className="bg-purple-700 text-white rounded-lg p-2">Copied</div>
  }

  const handleCopy = ()=>{
    var copyText = props.password;
    navigator.clipboard.writeText(copyText);
  }

  return (
    <div className='w-full'>
        <div className='custom-shadow flex justify-between items-center custom-bg p-5 rounded-2xl text-yellow-400'>
          <h1 id="display-content" className='text-2xl font-semibold'>{props.password}</h1>
          {issetTimeout(()=>{
            show()
          }, 2000)}
          <FontAwesomeIcon className="h-6 cursor-pointer" icon={faCopy} style={{color: "#23b2e1",}} onClick={handleCopy} />
        </div>
    </div>
  )
}

export default Display
